import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Form, Button } from 'reactstrap';
import './list.css';

function List() {
  const [carriers, setCarriers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    axios.get('http://localhost:8000/api/carriers')
      .then(response => {
        setCarriers(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const filteredCarriers = carriers.filter(carrier => {
    return carrier.name.toLowerCase().includes(searchTerm.toLowerCase());
  });

  const sortedCarriers = filteredCarriers.sort((a, b) => {
    if (sortOrder === 'asc') {
      return a.name.localeCompare(b.name);
    } else {
      return b.name.localeCompare(a.name);
    }
  });

  const handleSearchChange = e => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = e => {
    setSortOrder(e.target.value);
  };

  return (
    <div className="container my-5">
      <h1 className="mb-4">List of Carriers</h1>
      <div className="row mb-4">
        <div className="col-sm-6">
          <Form>
            <Form.Group controlId="searchTerm">
              <Form.Label>Search by Carrier Name</Form.Label>
              <Form.Control type="text" value={searchTerm} onChange={handleSearchChange} />
            </Form.Group>
          </Form>
        </div>
        <div className="col-sm-6">
          <Form>
            <Form.Group controlId="sortOrder">
              <Form.Label>Sort by Carrier Name</Form.Label>
              <Form.Control as="select" value={sortOrder} onChange={handleSortChange}>
                <option value="asc">Ascending</option>
                <option value="desc">Descending</option>
              </Form.Control>
            </Form.Group>
          </Form>
        </div>
      </div>
      <div className="row">
        {sortedCarriers.map(carrier => (
          <div key={carrier.id} className="col-sm-4 mb-4">
            <Card className="carrier-card">
              <Card.Body>
                <Card.Title>{carrier.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{carrier.stream}</Card.Subtitle>
                <Card.Text>{carrier.description}</Card.Text>
                <Button className="carrier-button" variant="primary">{carrier.interest}</Button>
              </Card.Body>
            </Card>
          </div>
        ))}
      </div>
    </div>
  );
}

export default List;
