import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './list.css';

function CarriersList() {
  const [carriers, setCarriers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    axios.get('http://localhost:8000/api/carriers')
      .then(response => {
        setCarriers(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const filteredCarriers = carriers.filter(carrier => {
    return carrier.name.toLowerCase().includes(searchTerm.toLowerCase());
  });

  const sortedCarriers = filteredCarriers.sort((a, b) => {
    if (sortOrder === 'asc') {
      return a.name.localeCompare(b.name);
    } else {
      return b.name.localeCompare(a.name);
    }
  });

  const handleSearchChange = e => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = e => {
    setSortOrder(e.target.value);
  };

  return (
    <div className="container">
      <h1>Carriers List</h1>
      <form>
        <label htmlFor="searchTerm">Search by Carrier Name</label>
        <input type="text" id="searchTerm" value={searchTerm} onChange={handleSearchChange} />
        <label htmlFor="sortOrder">Sort by Carrier Name</label>
        <select id="sortOrder" value={sortOrder} onChange={handleSortChange}>
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
      </form>
      <div className="card-container">
        {sortedCarriers.map(carrier => (
          <div className="card" key={carrier.id}>
            <h3 className="card-title">{carrier.name}</h3>
            <p className="card-text">Stream: {carrier.stream}</p>
            <p className="card-text">Interest: {carrier.interest}</p>
            <p className="card-text">{carrier.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CarriersList;
