import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Button } from 'reactstrap';

function Carriers() {
  const [carriers, setCarriers] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/api/carriers')
      .then(response => {
        setCarriers(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  function VideoThumbnail({ videoId }) {
    const [thumbnail, setThumbnail] = useState(null);

    useEffect(() => {
      async function getVideoDetails() {
        try {
          const key = "YOUR_YOUTUBE_API_KEY";
          const response = await axios.get(`https://www.googleapis.com/youtube/v3/videos?part=snippet&id=${videoId}&key=${key}`);
          const thumbnailUrl = response.data.items[0].snippet.thumbnails.medium.url;
          setThumbnail(thumbnailUrl);
        } catch (error) {
          console.error(error);
        }
      }

      getVideoDetails();
    }, [videoId]);

    return <img src={thumbnail} alt="Video Thumbnail" />;
  }

  return (
    <Container className="my-5">
      <h1 className="text-center mb-4">List of Carriers</h1>
      <Row>
        {carriers.map(carrier => (
          <Col key={carrier.id} lg={4} className="mb-4">
            <Card className="h-100 shadow-sm">
              <div className="embed-responsive embed-responsive-16by9">
                {carrier.youtube_video_id && (
                  <iframe
                    className="embed-responsive-item"
                    src={`https://www.youtube.com/embed/${carrier.youtube_video_id}`}
                    title={carrier.name}
                    allowFullScreen
                  />
                )}
              </div>
              <Card.Body>
                <Card.Title>{carrier.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{carrier.stream}</Card.Subtitle>
                <Card.Text>{carrier.description}</Card.Text>
                {carrier.more && (
                  <div className="my-2">
                    <Button color="primary" size="sm" onClick={() => window.open(carrier.more, "_blank")}>
                      Learn More
                    </Button>
                  </div>
                )}
                <Button color="secondary" size="sm" onClick={() => window.open(carrier.link, "_blank")}>
                  Watch on YouTube
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Carriers;
