import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form } from 'react-bootstrap';
import Card from 'react-bootstrap';
import Row from 'react-bootstrap';
import Col from 'react-bootstrap';



function CarriersList() {
  const [carriers, setCarriers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    axios.get('http://localhost:8000/api/carriers')
      .then(response => {
        setCarriers(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const filteredCarriers = carriers.filter(carrier => {
    return carrier.name.toLowerCase().includes(searchTerm.toLowerCase());
  });

  const sortedCarriers = filteredCarriers.sort((a, b) => {
    if (sortOrder === 'asc') {
      return a.name.localeCompare(b.name);
    } else {
      return b.name.localeCompare(a.name);
    }
  });

  return (
    <div>
      <h1>Carriers List</h1>
      <Form>
        <Form.Group controlId="searchTerm">
          <Form.Label>Search by Carrier Name</Form.Label>
          <Form.Control type="text" placeholder="Enter Carrier Name" defaultValue={searchTerm} onChange={e => setSearchTerm(e.target.value)} />
        </Form.Group>
        <Form.Group controlId="sortOrder">
          <Form.Label>Sort by Carrier Name</Form.Label>
          <Form.Control as="select" defaultValue={sortOrder} onChange={e => setSortOrder(e.target.value)}>
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </Form.Control>
        </Form.Group>
      </Form>
      <Row>
        {sortedCarriers.map(carrier => (
          <Col sm={6} key={carrier.id}>
            <Card>
              <Card.Body>
                <Card.Title>{carrier.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{carrier.stream}</Card.Subtitle>
                <Card.Text>{carrier.interest}</Card.Text>
                <Card.Text>{carrier.description}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
}

export default CarriersList;
